    private void boton_validarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_boton_validarActionPerformed
       try{
           String valor=tex_valor.getText();
           byte valorB[]=valor.getBytes();
           int tamano=valorB.length;
           Tamano.Tamano(tamano);
           Etiqueta.setText("Aceptado");
      }catch (ValidarException ex) {
            Etiqueta.setText(ex.getMessage()+" Tonto...");
        }
        
    }//GEN-LAST:event_boton_validarActionPerformed

